import { CheckoutSdkEventMap } from '@mangopay/checkout-sdk-hosted-core';
export interface CheckoutSdkInstance {
    isComplete: boolean;
    isDisabled: boolean;
    mount(elementOrSelector: HTMLElement | string): void;
    umount(): void;
    on<T extends keyof CheckoutSdkEventMap>(type: T, callback: (event: CheckoutSdkEventMap[T]) => void): () => void;
    disable(): void;
    enable(): void;
    completePayment: () => void;
    setLoading: (loading: boolean) => void;
}
