/// <reference types="applepayjs" />
export interface ApplePayPaymentRequests extends ApplePayJS.ApplePayPaymentRequest {
  merchantIdentifier: string;
  merchantName: string;
  onValidateMerchant?: (input: ValidateApplePayMerchantInput) => Promise<ApplePayMerchantSession>;
  supportedNetworks?: string[];
  merchantCapabilities?: ApplePayJS.ApplePayMerchantCapability[];
  captureBillingAddress?: boolean;
  requiredBillingContactFields?: ApplePayJS.ApplePayContactField[];
  requiredShippingContactFields?: ApplePayJS.ApplePayContactField[];
  billingContact?: ApplePayJS.ApplePayPaymentContact;
  shippingContact?: ApplePayJS.ApplePayPaymentContact;
}
export interface ApplePayMerchantSession {
  epochTimestamp: number;
  expiresAt: number;
  merchantSessionIdentifier: string;
  nonce: string;
  merchantIdentifier: string;
  domainName: string;
  displayName: string;
  signature: string;
}
export interface ApplePayPaymentMethodOptions {
  paymentRequest: ApplePayPaymentRequests;
}
export interface ValidateApplePayMerchantInput {
  validationURL: string;
}
export type ValidateApplePayMerchant =
  | ((input: ValidateApplePayMerchantInput) => Promise<ApplePayMerchantSession>)
  | undefined;
